INSERT INTO `es_brand` VALUES ('15', '五粮液', 'http://static.v4.javamall.com.cn/attachment/brand/201202221044295459.jpg', null, '<p>\n	五粮液集团有限公司前身是50年代初8家古传酿酒作坊联合组建而成的&ldquo;中国专卖公司四川省宜宾酒厂&rdquo;，1959年正式命名为&ldquo;宜宾五粮液酒厂&rdquo;，1998年改制为&ldquo;四川省宜宾五粮液集团有限公司&rdquo;。</p>\n', 'http://www.wuliangye.com.cn/', '0', '0');
INSERT INTO `es_brand` VALUES ('16', '阿尔卑斯', 'http://static.v4.javamall.com.cn/attachment/brand/201202221654250514.jpg', null, '<p>\n	<font size=\"-1\"><em>阿尔卑斯</em>全球最大的糖果生产企业之一，专业从事各种糖果和胶姆糖的生产和销售。其中比较有代表性的就是<em>阿尔卑斯</em>棒棒糖和<em>阿尔卑斯</em>糖。<em>阿尔卑斯</em>糖表达爱只需一颗<em>阿尔卑斯</em>。</font></p>\n', 'http://www.acandy.cn/', '0', '0');
