INSERT INTO `es_brand` VALUES ('25', '郎酒', null, null, '<p>\n	四川郎酒集团，是一个以生产销售中国名酒郎酒和房地产开发的大型现代化企业集团。</p>\n<p align=\"left\">\n	郎酒，始于1903年，产自川黔交界有&ldquo;中国美酒河&rdquo;之称的赤水河畔，是目前中国唯一一家采用山泉水酿造的白酒企业。</p>\n<p>\n	郎酒历史可追溯到汉武帝时期，当时宫廷贡酒&ldquo;枸酱酒&rdquo;即是郎酒前身。从&ldquo;絮志酒厂&rdquo;、&ldquo;惠川糟房&rdquo;到&ldquo;集义糟房&rdquo;的&ldquo;回沙郎酒&rdquo;开始，至今已有100多年的悠久历史。</p>\n', 'http://www.langjiu.cn/', '0', '0');
