INSERT INTO `es_goods` VALUES ('16', '百味林 葡萄干245g', '0000106934', '4', '7', '2', '0', null, '245.00', '1', null, '<div class=\"detail_box\">\n	<p class=\"detail_tit\">\n		<strong>商品特色</strong></p>\n	<p>\n		<font face=\"宋体\" size=\"2\">葡 萄干为葡萄科葡萄属植物各种葡萄的果实，又名草龙珠、蒲桃。主产新疆、甘肃，陕西、河北、山东等地均有栽培。夏末秋初采收，鲜用或干燥备用。不少干果都有 其促进人体健康的绝招，而葡萄干所含的营养成分更是令营养学家赞叹不已。葡萄干的可防范眩晕、心悸、乏力等低血糖反应症状。葡萄干中的铁和钙含量十分丰 富，是儿童、妇女及体弱贫血者的滋补佳品，可补血气、暖肾，治疗贫血，血小板减少；内含大量葡萄糖，对心肌有营养作用，有助于冠心病患者的康复；另外葡萄 干还含有多种矿物质和维生素、氨基酸，常食对神经衰弱和过度疲劳者有较好的补益作用，还是妇女病的食疗佳品。 </font></p>\n</div>\n<p class=\"detail_tit\">\n	<strong>品牌介绍</strong></p>\n<p style=\"margin-left: 10px; line-height: 180%; margin-right: 10px\">\n	<span class=\"c\">&nbsp;上 海百味林实业有限公司是上海南洪集团下属的子公司，主要涉及休闲食品的生产和销售。截止目前，公司所开发的百味林系列食品近300余种，深受广大消费者喜 爱。百味林公司的销售网络遍及全国各地，除标准商超，卖场外，更开发有连锁专卖店等多种经营模式，年销售逾10亿。&nbsp;&ldquo;百味林&rdquo;品牌为上海市著名商标，上 海市名优食品，是炒货协会会员单位。 </span></p>\n', '14.00', '12.00', '15.00', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"\",\"valueList\":[]},{\"name\":\"重量\",\"value\":\"245g\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"上海\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"365天\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"优质葡萄干\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":6}]', '[{\"cost\":12,\"goodsLvPrices\":[],\"goods_id\":16,\"name\":\"百味林 葡萄干245g\",\"price\":14,\"product_id\":16,\"sn\":\"0000106934\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":245}]', null, null, '1329895662', '1329895662', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '中国', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202221526415356_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221526415356_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221526415356_small.jpg', 'http://static.v4.javamall.com