INSERT INTO `es_goods` VALUES ('17', '百味林苏式话梅200G', '0000380030', '4', '7', '2', '0', null, '200.00', '1', null, '<p>\n	<font face=\"宋体\"><font size=\"3\"><font color=\"#800000\"><strong><font color=\"#000000\" face=\"Arial\" style=\"background-color: #c0c0c0\">&nbsp;</font>商品特色</strong></font>&nbsp; <font color=\"#c0c0c0\">Product</font></font></font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">百味林苏式话梅肉质较软，色泽鲜艳，味酸甜可口，芳香扑鼻，入口后能止渴生津，特别是在夏季炎暑时，吃此品更觉清爽适口，一般孕妇尤喜食用。<br />\n	&nbsp;<br />\n	话梅小知识：用芒种后采摘的黄熟梅子，俗称黄梅。黄梅从树上采下来洗净后，放在大缸里用盐水泡浸月余，取出晒干；晒干后再用清水漂洗，再晒干；然后用糖料泡腌，再晒干―――如此多次反复，可谓是&ldquo;十蒸九晒，数月一梅&rdquo;，最后成为肉厚干脆、甜酸适度的话梅。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">1.饭后偶尔吃一两颗是可以促进消化。<br />\n	2.梅子含油脂低，含钠低，不含胆因醇，而且富含纤维质、钾、钙，维生素A、B1与B2，是大自然送给我们的绝好食品。<br />\n	3.梅子的高纤维质含量使它俨然是一种天然的软便剂，是改善便秘的一种好食物。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">小提示：人们每天吃10粒左右的梅子就满足了我们人体一天对纤维质的需求。对希望保持身材的女性来说，更是一个福音。同时，梅子的高含铁量，也可以补充青春期少女的高铁质需求量。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>温馨提示：</strong><br />\n	由于部分商品包装更换较为频繁，因此您收到的货物有可能与图片不完全一致，请您以收到的商品实物为准，同时我们会尽量做到及时更新，由此给您带来不便请多多谅解，谢谢！</font></p>\n', '10.00', '6.00', '15.00', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"\",\"valueList\":[]},{\"name\":\"重量\",\"value\":\"200g\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"上海\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"365天\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"优质话梅\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"存放于阴凉干爽处，避免阳光直射\",\"valueList\":[]}],\"paramNum\":6}]', '[{\"cost\":6,\"goodsLvPrices\":[],\"goods_id\":17,\"name\":\"百味林苏式话梅200G\",\"price\":10,\"product_id\":17,\"sn\":\"0000380030\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":200}]', null, null, '1329895874', '1329895874', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '3', '中国', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment