INSERT INTO `es_goods` VALUES ('52', '百威 啤酒330ml/罐', '0009000366', '27', '20', '13', '0', null, '330.00', '1', null, '<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"4\"><strong>品牌介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Brand</font><br />\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">始终如一的卓越品质是百威的标志，无论在世界任何地方酿制的百威啤 酒都能保证同样清澈、清醇、清爽的绝佳口感。为了确保品质的统一，百威啤酒的总部安海斯－布希公司要求其旗下的12家美国本土的酿酒厂、2家海外百威生产 厂以及国外12家合作生产厂家严格遵循精密的生产工序，从最初挑选上好的全天然原料到最后的品味测试，百威共有240道保质工序。</font></p>\n<p>\n	<font color=\"#000000\" face=\"宋体\" size=\"2\">百威始终不计成本地选用最优质的全天然原料，包括麦芽、啤酒花、大米、酵母和水，酿制百威的后成熟工序是公司独一无二的山毛榉木陈酿工艺，这种传统技术确保了百威啤酒的天然口味。</font></p>\n', '5.40', '5.00', '6.20', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格(重量)\",\"value\":\"330ml\",\"valueList\":[]},{\"name\":\"包装\",\"value\":\"听\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"武汉\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"一年\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"存放于阴凉干爽处，避免阳光直射\",\"valueList\":[]},{\"name\":\"商品形态\",\"value\":\"液态\",\"valueList\":[]},{\"name\":\"食用方法\",\"value\":\"开瓶饮用\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"水、麦芽、啤酒花\",\"valueList\":[]}],\"paramNum\":8}]', '[{\"cost\":5,\"goodsLvPrices\":[],\"goods_id\":52,\"name\":\"百威 啤酒330ml/罐\",\"price\":5.4,\"product_id\":52,\"sn\":\"0009000366\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":330}]', null, null, '1329910645', '1329910645', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202221935181468_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221935181468_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221935181468_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221935181468.jpg');
INSERT INTO `es_goods` VALUES ('53', '百威 啤酒330ml*6/罐', '0015213964', '27', '20', '13', '0', null, '330.00', '1', null, '<p class=\"detail_tit\">\n	品牌介绍</p>\n<p>\n	百威啤酒诞生于1876年， 由阿道弗斯&middot;布希创办。它采用质量最佳的纯天然 材料，以严谨的工艺控制，通过自然发酵，低温储藏而酿成。整个生产流程中不使用任何人造成份、添加剂或防腐剂。在发酵过程中，又使用数百年传统的山毛榉木 发酵工艺，使啤酒格外清爽。百年发展中一直以其纯正的口感，过硬的质量赢得了全世界消费者的青睐，成为世界最畅销、销量最多的啤酒，长久以来被誉为是&ldquo;啤 酒之王&rdquo;！</p>\n', '34.80', '34.00', '36.90', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格(重量)\",\"value\":\"330ml\",\"valueList\":[]},{\"name\":\"包装\",\"value\":\"听\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"武汉\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"一年\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"存放于阴凉干爽处，避免阳光直射\",\"valueList\":[]},{\"name\":\"商品形态\",\"value\":\"液态\",\"valueList\":[]},{\"name\":\"食用方法\",\"value\":\"开瓶饮用\",\"valueList\":[]},{\"name\":\"成分\",\"value\":\"水、麦芽、啤酒花\",\"valueList\":[]}],\"paramNum\":8}]', '[{\"cost\":34,\"goodsLvPrices\":[],\"goods_id\":53,\"name\":\"百威 啤酒330ml*6/罐\",\"price\":34.8,\"product_id\":53,\"sn\":\"0015213964\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":330}]', null, null, '1329910846', '1329910846', '0', '0', '0', '100', '100', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202221938556007_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221938556007_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221938556007_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202221938556007.jpg');
