INSERT INTO `es_goods` VALUES ('101', '相宜本草 相宜去死皮膏68g/支', '0000089725', '56', '40', '24', '0', null, '68.00', '1', null, '<p class=\"detail_tit\">\n	品牌介绍</p>\n<p>\n	<font size=\"2\"><font face=\"宋体\">&nbsp;&nbsp;&nbsp;&nbsp;相 宜本草&mdash;个诠释;本草养肤;概念的品牌。其创始人封帅女士，乃中医名家之后，其外祖父杨继田先生为泰山脚下一代名医，曾任冯玉祥将军的御用医师，医术造诣 深厚，医德馨名远扬。封帅女士自幼深受中医文化熏陶，秉承对中医文化与汉方美颜的一份责任，于1999年创立了相宜本草品牌。中医强调的;标本兼治;理 念，正是相宜本草养肤之道的来由。 因为，女人的美，从来都是由内而外。 即使外表平凡，强大的内在力量亦让美丽绽放；倘若丽质天生，内在力量更令你魅力加分。 肌肤更是如此，强韧的肌肤能量，内在的活力和健康，才是肌肤真正的美丽源泉。<br />\n	&nbsp;&nbsp;&nbsp; 相宜本草与上海中医药大学基础医学院长期合作，创立本草护肤研究中心。通过研究大量养颜古方秘方，不断探索发现本草护肤养颜的独到功效。历经十年致力于本 草护肤品的研究，独创肌芯养肤科技将汉方本草和现代科技相结合，开发出一系列功效卓著，使用安全本草护肤品。以内在力，外在美为核心的本草养肤之道，正是 由此诞生。肌芯养肤科技&rdquo;能让肌肤内在重获健康，不再依赖外在的修饰和维持，自然、主动、持久地筑起肌肤外在的美丽。相宜本草让肌肤之美从此生生不息。</font></font></p>\n', '25.00', '22.00', '29.30', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"68g\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"2008-08-25\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"二年\",\"valueList\":[]},{\"name\":\"产品成分\",\"value\":\"\",\"valueList\":[]},{\"name\":\"保存方法\",\"value\":\"于干燥处常温保存\",\"valueList\":[]}],\"paramNum\":6}]', '[{\"cost\":22,\"goodsLvPrices\":[],\"goods_id\":101,\"name\":\"相宜本草 相宜去死皮膏68g/支\",\"price\":25,\"product_id\":101,\"sn\":\"0000089725\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":68}]', null, null, '1329984765', '1329984765', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '去死皮', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202231610512068_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202231610512068_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202231610512068_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202231610512068.jpg');
