INSERT INTO `es_goods` VALUES ('164', '美赞臣安婴宝A+较大婴儿配方奶粉400g/盒（6-12个月）', '0000127093', '63', '59', '28', '0', null, '400.00', '1', null, '<p class=\"detail_tit\">\n	<strong>品牌介绍</strong></p>\n<p>\n	&nbsp;&nbsp;&nbsp; 美赞臣现在在世界上有多个生产基地，产品行销一百多个国家和地区，在美洲、欧洲和亚洲的主要国家和地区都有美赞臣产品。象世界上其他的大型跨国企业一样， 发展新产品以满足顾客需求是美赞臣另一经营方针，美赞臣每年投入大量的费用研究医学和营养课题，通过细致的市场调查以了解消费者以及医学界专家，如：医 生、护士、营养师等的实际需求之后，确保美赞臣的产品满足当代人的需要。</p>\n<p>\n	&nbsp;&nbsp;&nbsp; 配方奶粉是美赞臣的拳头产品之一。安婴宝是美国销量冠军，是医生乐意推荐的婴儿配方奶粉。作为营养界先锋。近一个世纪以来，由美赞臣首先开发产品多不胜 数。在未来的日子里，美赞臣定会一如既往，继续努力不懈地向全世界的消费者提供最高品质的营养品，确保您的选择一定是最好的。</p>\n', '90.00', '85.00', '90.00', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"6-12个月\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"2008-09-17\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"540天\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"广州\",\"valueList\":[]},{\"name\":\"配方\",\"value\":\"乳糖、精制植物油、浓缩乳清蛋白、脱脂奶粉、GOS（低聚半乳糖）、ARA、DHA、卵磷脂、核苷酸、抗氧化剂、肌醇、玉米糖浆固体、牛磺酸、矿物质、维生素。\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":85,\"goodsLvPrices\":[],\"goods_id\":164,\"name\":\"美赞臣安婴宝A+较大婴儿配方奶粉400g/盒（6-12个月）\",\"price\":90,\"product_id\":164,\"sn\":\"0000127093\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":400}]', null, null, '1330003136', '1330003136', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', '6-12个月', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202232117190226_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232117190226_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232117190226_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232117190226.jpg');
