INSERT INTO `es_goods` VALUES ('174', '多美滋 金装优衡多营养幼儿配方奶粉（1~3岁）900g/罐', '0009966818', '62', '61', '28', '0', null, '900.00', '1', null, '<div class=\"detail_box\">\n	<p class=\"detail_tit\">\n		<strong>商品特色</strong></p>\n	<p>\n		全面而均衡的膳食应包含来自六大类营养的广泛食物品种。尽管1岁以上的宝宝开始吃各种不同的食物，但宝宝的胃口有限，所以你需要多美滋优衡多营养，每天只需多一杯，就能为宝宝提供55种天然食物和营养，满足宝宝生长发育的营养需求。</p>\n</div>\n<div class=\"detail_box\">\n	<p class=\"detail_tit\">\n		<strong>适用范围</strong></p>\n	<p>\n		1-3岁</p>\n</div>\n<p class=\"detail_tit\">\n	<strong>使用方法</strong></p>\n<p>\n	1、准备喂哺前，先将双手清洗干净。<br />\n	2、将干净的奶瓶，奶嘴、瓶盖等用具放入沸水中煮沸5分钟，凉至室温。<br />\n	3、将饮用水煮沸5分钟并凉至60℃左右，将所需份量的温水倒入已消毒过的奶瓶。<br />\n	4、按建议食用量、量取正确份量的奶粉倒入奶瓶中。<br />\n	5、盖好瓶盖，摇奶瓶至奶粉完全溶解。</p>\n', '185.00', '180.00', '189.00', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"1~3岁\",\"valueList\":[]},{\"name\":\"上架时间\",\"value\":\"2010-06-03\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"18个月\",\"valueList\":[]},{\"name\":\"产地\",\"value\":\"上海\",\"valueList\":[]},{\"name\":\"配方\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":180,\"goodsLvPrices\":[],\"goods_id\":174,\"name\":\"多美滋 金装优衡多营养幼儿配方奶粉（1~3岁）900g/罐\",\"price\":185,\"product_id\":174,\"sn\":\"0009966818\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":900}]', null, null, '1330005097', '1330005097', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '1', '1~3岁', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202232150184198_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232150184198_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232150184198_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202232150184198.jpg');
