INSERT INTO `es_goods` VALUES ('190', '雀巢 营养米粉250g ', '0000238868', '66', '67', '32', '0', null, '250.00', '1', null, '<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"3\"><strong>商品介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Product</font></p>\n<p>\n	&nbsp;</p>\n<p>\n	<font face=\"宋体\" size=\"2\">规格：250G<br />\n	保质期：720天<br />\n	产地：黑龙江<br />\n	保存方法：避光</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">商品特色：</font><br />\n	<font face=\"宋体\" size=\"2\">科学配方，优越营养：婴儿期宝宝的消化系统还未发育成熟，选择营养丰富，易消化的食物 至关重要。婴儿期是宝宝昧觉发育最灵敏的阶段，通过尝试不同口味的食物，有助于培养宝宝良好的饮食习惯，同时获得丰富的营养。精选原料，卓越品质：雀巢营 养米粉精选来自黑龙江北大荒的优质大米（绿色食品），在生产过程中恪守严格的品质管理（HACCP质量控制）体系确保符合婴幼儿食用。给宝宝在成长的关健 时期，奠定优越基础。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">雀巢营养米粉含20多种营养素，如钙、铁、锌、以及合理比例的维生素A、B、C、D等，科学的营养组合，帮助宝宝轻松吸收营养，健康成长！</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">特别添加DHA有益大脑及视觉发育。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>配料：</strong></font><font face=\"宋体\" size=\"2\">大米粉、玉米油、碳酸钙、磷酸氢二钾，精炼金枪鱼油，维生素（C，烟酸，E，B1，B2，A，B6、叶酸，生物素，D3，B12），精炼金枪鱼油，富马酸亚铁，硫酸锌（可能含有微量谷蛋白）。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>调配方法：</strong></font><br />\n	<font face=\"宋体\" size=\"2\">1、清洁双手，取出已消毒的宝宝餐具备用。</font><br />\n	<font face=\"宋体\" size=\"2\">2、依建议喂哺量准备适量温奶放入碗中。</font><br />\n	<font face=\"宋体\" size=\"2\">3、加入适量米粉，放置15-30秒，待其充分吸水软化。</font><br />\n	<font face=\"宋体\" size=\"2\">4、用汤勺搅拌成均匀糊状，即可喂哺宝宝。&nbsp;&nbsp; </font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>建议喂哺方案：</strong></font><br />\n	<font face=\"宋体\" size=\"2\">每次喂哺：米粉（克）5 温水（毫升）35&nbsp; 每日餐数 1。</font><br />\n	<font face=\"宋体\" size=\"2\">半个月后：米粉（克）15 温水（毫升）95 每日餐数1-2 。&nbsp;</font><br />\n	<font face=\"宋体\" size=\"2\">一个月后 米粉（克）25 温水（毫升）160 每日餐数1-2。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">*喂哺方案仅供参考，您可以根据宝宝的月龄和食量以及&ldquo;由稀到稠，由少到多，同细到粗&rdquo;的原则，作适当调整。*每汤勺约7克奶米粉</font><font face=\"宋体\" size=\"2\">。</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\"><strong>贮存方法：</strong>密封，置于阴凉，干燥处</font><font face=\"宋体\" size=\"2\">。</font></p>\n<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"3\"><strong><font color=\"#000000\" face=\"Arial\" size=\"3\" style=\"background-color: #c0c0c0\">&nbsp;</font>品牌介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Brand</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">拥有140年婴幼儿营养经验的雀巢，正在关注中国宝宝营养的一个更深层次的问题，那就是如何让 宝宝将吃下去的食物和营养更好地消化，更好的吸收。2006年，瑞士雀巢研究中心对雀巢成长系列奶粉的配方全面升级,雀巢成长系列奶粉的升级配方中独特的 &ldquo;活性BL益生菌＋膳食纤维&rdquo;组合，能够全面促进肠道健康，全面激发营养活力，让吸收更有效！</font></p>\n', '11.20', '10.00', '11.50', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"规格\",\"value\":\"250G\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"720天\",\"valueList\":[]},{\"name\":\"原产地\",\"value\":\"黑龙江\",\"valueList\":[]},{\"name\":\"储藏方法\",\"value\":\"避光\",\"valueList\":[]},{\"name\":\"配料\",\"value\":\"大米粉、玉米油、碳酸钙、磷酸氢二钾，精炼金枪鱼油，维生素（C，烟酸，E，B1，B2，A，B6、叶酸，生物素，D3，B12），精炼金枪鱼油，富马酸亚铁，硫酸锌（可能含有微量谷蛋白）\",\"valueList\":[]}],\"paramNum\":5}]', '[{\"cost\":10,\"goodsLvPrices\":[],\"goods_id\":190,\"name\":\"雀巢 营养米粉250g \",\"price\":11.2,\"product_id\":190,\"sn\":\"0000238868\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":250}]', null, null, '1330081679', '1330081679', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202241906389487_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202241906389487_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202241906389487_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202241906389487.jpg');
