INSERT INTO `es_goods` VALUES ('209', '伊威 全机能营养饼干150g ', '0000220020', '76', '70', '34', '0', null, '150.00', '1', null, '<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"4\"><strong>商品介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Product</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">规格：11g*8</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">保质期：360天</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">适用年龄：适合萌牙期的宝宝</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">产地：上海</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">配料：小麦粉<span lang=\"EN-US\">,</span>鲜鸡蛋<span lang=\"EN-US\">,</span>全脂奶粉<span lang=\"EN-US\">,</span>葡萄糖<span lang=\"EN-US\">,</span>荞麦<span lang=\"EN-US\">,</span>奶油<span lang=\"EN-US\">,</span>酵母</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">宝宝长牙特征：流口水<span lang=\"EN-US\">; </span>牙龈肿胀<span lang=\"EN-US\">; </span>烦躁<span lang=\"EN-US\">,</span>易怒<span lang=\"EN-US\">; </span>喜咬硬物<span lang=\"EN-US\">; </span>拒绝进食&nbsp;</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">注意事项：<span lang=\"EN-US\">1.</span>宝宝进食时家长必须在旁监护<span lang=\"EN-US\">; 2.</span>请勿给宝宝碎的磨牙棒<span lang=\"EN-US\">; 3.</span>宝宝进食时必须保持坐立姿势<span lang=\"EN-US\">,</span>不能躺着以免被噎<span lang=\"EN-US\">;</span></font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">产品特点：1.坚硬的表面可以锻炼牙龈及颌骨<span lang=\"EN-US\">,</span>满足宝宝出牙时咬和咀嚼的需要<span lang=\"EN-US\">; 2.</span>手指棒状<span lang=\"EN-US\">,</span>有助于锻炼宝宝抓握能力<span lang=\"EN-US\">; 3.</span>设计合理<span lang=\"EN-US\">,</span>更方便<span lang=\"EN-US\">,</span>更卫生<span lang=\"EN-US\">;</span></font></p>\n<p>\n	<font color=\"#800000\" face=\"黑体\" size=\"4\"><strong><font color=\"#000000\" face=\"Arial\" size=\"3\" style=\"background-color: #c0c0c0\">&nbsp;</font>品牌介绍</strong></font>&nbsp; <font color=\"#c0c0c0\" size=\"4\">Brand</font></p>\n<p>\n	<font face=\"宋体\" size=\"2\">&ldquo;伊威&rdquo;品牌原意为&ldquo;依偎&rdquo;，即是让宝宝依偎在妈妈的怀抱中，表现出伊威辅食以宝宝为中心，关爱宝宝成长和健康的理念。伊威成立于<span lang=\"EN-US\">1996</span>年。作为中国较早研制婴幼儿专业辅食系列产品的企业，伊威专注于生产适合中国宝宝体质的天然辅食。公司成立至今一直以严谨，爱心，高品质为导向，以服务为宗旨，兢兢业业，努力创新，为中国宝宝提供优质全面的营养辅食，也为妈妈们提供了更方便、更安全的喂哺方式<font face=\"宋体\" size=\"2\">。</font></font></p>\n', '38.50', '38.00', '38.50', '[{\"name\":\"基本参数\",\"paramList\":[{\"name\":\"上架时间\",\"value\":\"2009-05-11\",\"valueList\":[]},{\"name\":\"适用年龄\",\"value\":\"宝宝营养饼干\",\"valueList\":[]},{\"name\":\"保质期\",\"value\":\"\",\"valueList\":[]},{\"name\":\"配料\",\"value\":\"\",\"valueList\":[]}],\"paramNum\":4}]', '[{\"cost\":38,\"goodsLvPrices\":[],\"goods_id\":209,\"name\":\"伊威 全机能营养饼干150g \",\"price\":38.5,\"product_id\":209,\"sn\":\"0000220020\",\"specList\":[],\"specs\":\"\",\"specsvIdJson\":\"[]\",\"store\":0,\"weight\":150}]', null, null, '1330086648', '1330086648', '0', '0', '0', '0', '0', '0', '', '', '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '150g', '0', '0', '0', null, '0', 'http://static.v4.javamall.com.cn/attachment/goods/201202242029262293_thumbnail.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202242029262293_big.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202242029262293_small.jpg', 'http://static.v4.javamall.com.cn/attachment/goods/201202242029262293.jpg');
